DROP TABLE IF EXISTS Students;
CREATE TABLE IF NOT EXISTS Students (
    student_id        INTEGER PRIMARY KEY,                                                  -- 学号
    student_name      TEXT,                                                                 -- 学生姓名
    sex               Integer check (sex=1 or sex=0) DEFAULT 1,                             -- 性别，1是男，0是女
    age               INTEGER check (age between 0 and 100),                                -- 年龄
    college_id        INTEGER,                                                              --学院
    class_id          INTEGER,                                                              -- 班级编号
    birthdate         DATE                           DEFAULT NULL,                          -- 出生日期
    registration_date DATE NOT NULL                  DEFAULT (datetime('now','localtime')), -- 注册日期
    address           TEXT                           DEFAULT NULL,                          -- 地址
    FOREIGN KEY (class_id) REFERENCES Class(class_id),FOREIGN KEY (college_id) REFERENCES College(college_id)
);
DROP INDEX IF EXISTS Students_Index;
CREATE UNIQUE INDEX IF NOT EXISTS Students_Index ON Students(student_id);
