DROP TABLE IF EXISTS TeacherCourse;
CREATE TABLE IF NOT EXISTS TeacherCourse ( -- 教师授课表，多对多关系
    course_id  INTEGER,                    -- 课程编号
    teacher_id INTEGER,                    -- 教师编号
    PRIMARY KEY (teacher_id,course_id),
    FOREIGN KEY (course_id) REFERENCES Course(course_id),
    FOREIGN KEY (teacher_id) REFERENCES Teacher(teacher_id)
);
DROP INDEX IF EXISTS TeacherCourse_Index;
CREATE UNIQUE INDEX IF NOT EXISTS TeacherCourse_Index ON TeacherCourse(teacher_id,course_id);
